package com.bridgelabz.demo;

public class EvenOrOddNumber {
    public static void main(String[] args) {

        // TODO Auto-generated method stub
        int i = 6;
        if (i % 2 == 0) {
            System.out.println("number is even");
        } else {
            System.out.println("number is odd");
        }
    }
}
